import React, { useState } from 'react'
import { UploadImage } from './requests.jsx'

const Logo = (props: any) => {
  const [image, setImage] = useState({ image_file: null, image_url: "" })

  const sendFile = () => {
    console.log(typeof image, image.image_file)
    UploadImage(image)
  }

  const handleFile = (e: any) => {
    let image = e.target.files[0]
    console.log(typeof image)
    let reader = new FileReader()
    reader.readAsDataURL(image)
    console.log(typeof image)
    setImage({
      image_file: image,
      image_url: URL.createObjectURL(image)
    })
    console.log("Logo: image: ", URL.createObjectURL(image), URL.createObjectURL(image).length)
    reader.onload = (e: any) => {
      console.log(e.target.result)
      setImage({
        image_file: e.target.result,
        image_url: URL.createObjectURL(image)
      })
    }
    console.log({
      image_file: e.target.result,
      image_url: URL.createObjectURL(image)
    })

  }

  return (
    // <div>
    //   <input type="file" name="image" onChange={handleFile}></input>
    //   <button type="button" onClick={sendFile}> HI </button>
    // </div>
    //enctype="multipart/form-data"
    <div>
      <img src={image.image_file} />
        <input type="file" onChange={handleFile} name="image" />
          <button type="button" onClick={sendFile} value="upload file.."/>
      </div>
          )
        }

        export default Logo
