import React, { useState, useEffect } from 'react'
import { UploadProfileImage, GetProfileImage } from './requests.jsx'
var im = require('imagemagick');

const Logo = (props: any) => {
  const [image, setImage] = useState(null)

  var { picture } = GetProfileImage(props.valueUser)
  useEffect(() => {
    if (props.valueUser != null) {
      setImage(picture)
    }
  }, [picture])
  const sendFile = () => {
    UploadProfileImage(props.valueUser, image)
  }

  const handleFile = (e: any) => {
    let image = e.target.files[0]
    // let imageUrl = URL.createObjectURL(image)
    im.convert(['kittens.jpg', '-resize', '25x120', 'kittens-small.jpg'],
      function(err: any, stdout: any) {
        if (err) throw err;
        console.log('stdout:', stdout);
      });


    let reader = new FileReader()
    reader.readAsDataURL(image)
    reader.onload = (e: any) => {
      let base64 = e.target.result
      console.log(base64)
      setImage(base64)
    }
  }

  return (
    <div className="profile">
      <img src={image} className="profileImage" />
      <input type="file" onChange={handleFile} className="changeProfileImage" name="image" />
      <button type="button" onClick={sendFile} value="upload file.." />
    </div>
  )
}

export default Logo
