import React, { useState, useCallback } from 'react'
import Cropper from 'react-easy-crop'
import Slider from 'react-easy-crop'

const Croper = (props: any) => {
  const [crop, setCrop] = useState({ x: 0, y: 0 })
  const [zoom, setZoom] = useState(1)
  const onCropComplete = useCallback((croppedArea, croppedAreaPixels) => {
    console.log(croppedArea, croppedAreaPixels)
  }, [])
  return (
    <div>
    <div className="crop-container">
      <Cropper image="https://img.huffingtonpost.com/asset/5ab4d4ac2000007d06eb2c56.jpeg?cache=sih0jwle4e&ops=1910_1000" crop={crop} zoom={zoom} aspect={4 / 3} onCropChange={setCrop} onCropComplete={onCropComplete} onZoomChange={setZoom} />
    </div>
    <div className="controls">
      {/* <Slider value={zoom} min={1} max={3} step={0.1} aria-labelledby="Zoom" onChange={(e: any, zoom: any) => setZoom(zoom)} classes={{
        container: 'slider'
      }} /> */}
    </div>
  </div >)
}

export default Croper
