import React, { useState, useEffect } from 'react'
import { UploadProfileImage, GetProfileImage } from './requests.jsx'
var fs = require('fs')
  , gm = require('gm').subClass({ imageMagick: true });

const Logo = (props: any) => {
  const [image, setImage] = useState(null)

  var { picture } = GetProfileImage(props.valueUser)
  useEffect(() => {
    if (props.valueUser != null) {
      setImage(picture)
    }
  }, [picture])
  const sendFile = () => {
    UploadProfileImage(props.valueUser, image)
  }

  const handleFile = (e: any) => {
    let image = e.target.files[0]
    gm(image)
      .resize(100, 100)
      .noProfile()
      .write('/path/to/resize.png', function(err: any) {
        if (!err) console.log('done');
      });


    let reader = new FileReader()
    reader.readAsDataURL(image)
    reader.onload = (e: any) => {
      setImage(
        e.target.result
      )
      console.log(e.target.result)
    }
  }

  return (
    <div className="profile">
      <img src={image} className="profileImage" />
      <input type="file" onChange={handleFile} className="changeProfileImage" name="image" />
      <button type="button" onClick={sendFile} value="upload file.." />
    </div>
  )
}

export default Logo
