  console.log("AddUser: bodyContent: ", bodyContent)
  fetch(url, {
    method: 'POST',
    headers: headersContent,
    credentials: 'same-origin',
    body: JSON.stringify(bodyContent)
  }).then(data => data.text()).then(text => JSON.parse(text)).then(json => {
    console.log("AddUser: ", json)
    return json
  })
}

