  useEffect(() => {
    var bodyContent = {
      name: user
    }
    setState(state => ({data: state.data, loading: true}))
    fetch(url, {
      method: 'POST',
      headers: headersContent,
      credentials: 'same-origin',
      body: JSON.stringify(bodyContent)
    }).then(data => data.text()).then(text => JSON.parse(text)).then(json => {
