import React, {useState} from 'react'
import Cropper from 'react-easy-crop'
const Croper = (props) => {
  const [state, setState] = useState({
    image: props.image,
    crop: {
      x: 0,
      y: 0
    },
    zoom: 1,
    aspect: 16 / 9
  })

  const onCropChange = (crop) => {
    setState({crop})
  }

  const onCropComplete = (croppedArea, croppedAreaPixels) => {
    console.log(croppedArea, croppedAreaPixels)
  }

  const onZoomChange = (zoom) => {
    setState({zoom})
  }
  const style = {
    position: "relative"
  }
  return (
    <div style={style}>
    // {console.log("Croper: state.image: ", props.image)}
    <Cropper image={"https://www.google.com/images/branding/googlelogo/2x/googlelogo_color_272x92dp.png"} crop={{
        x: 0,
        y: 0
      }} zoom={1} aspect={16 / 9} onCropChange={onCropChange} onCropComplete={onCropComplete} onZoomChange={onZoomChange}/>
  </div>
  )
}

export default Croper
