import React, { useState, useEffect } from 'react'
import { DoesUserExist } from './requests.jsx'

const LoginInputs = (props: any) => {
  const [storeUser, setStoreUser] = useState(localStorage.getItem("user"))
  const [finalUser, setFinalUser] = useState("")
  const [userExists, setUserExists] = useState("")
  const [send, setSend] = useState(0)
  var data: any
  const handleInput = (event: any) => {
    setStoreUser(event.target.value)
  }
  var { DoesExist } = DoesUserExist(storeUser, finalUser)
  // TODO: this doesnt work
  const sendInput = () => {
    // console.log("LoginInputs: newuser " + storeUser)
    // console.log("LoginInputs: send: ", send)
    // let newSend = send + 1
    // console.log("LoginInputs: newSend: ", newSend)
    // setSend(newSend)
    // console.log("LoginInputs: send: ", send)
    setFinalUser(storeUser)
    console.log("LoginInputs: send: ", finalUser)
    if (DoesExist) {
      console.log("LoginInputs: SendInput: data ", DoesExist)
      props.saveUser(storeUser)
      props.setAlreadySet()
      DoesExist = false
    }
  }



  const url = 'http://192.168.0.16:5000/doesuserexist';
  const [state, setState] = useState({ DoesExist: null, loading: true })
  useEffect(() => {
    var bodyContent = {
      name: finalUser
    }
    console.log("DoesUserExist: bodyContent: ", finalUser)
    setState(state => ({ DoesExist: state.DoesExist, loading: true }))
    fetch(url, {
      method: 'POST',
      credentials: 'same-origin',
      body: JSON.stringify(bodyContent)
    }).then(data => data.text()).then(text => JSON.parse(text)).then(json => {
      console.log("DoesUserExist: json data", json, finalUser)
      setState({ DoesExist: json, loading: false })
    })
  }, [finalUser])
  data = state

  return (
    <div className="logins">
      <input type="text" onChange={handleInput} className="loginInputs" placeholder="Username"></input>
      <button type="button" onClick={sendInput} className="loginInputs">Send</button>
      <span> {userExists} </span>
    </div>
  )
}

export default LoginInputs
